'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:ynumticks' );
var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isPrimitive;
var isNull = require( '@stdlib/utils/is-null' );


// SET //

/**
* Sets the number of y-axis tick marks.
*
* @private
* @param {(NonNegativeInteger|null)} ticks - number of ticks
* @throws {TypeError} must be a nonnegative integer or null
*/
function set( ticks ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isNonNegativeInteger( ticks ) && !isNull( ticks ) ) {
		throw new TypeError( 'invalid value. `yNumTicks` must be a nonnegative integer or null. Value: `' + ticks + '`.' );
	}
	debug( 'Current value: %d.', this._yNumTicks );
	oldVal = this._yNumTicks;

	this._yNumTicks = ticks;
	debug( 'New value: %d.', this._yNumTicks );

	this.emit( 'yNumTicksChanged', ticks, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
