'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:ytickformat' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var isNull = require( '@stdlib/utils/is-null' );
var events = require( './../events' );


// VARIABLES //

var CHANGE_EVENT = events( 'yTickFormat' );


// SET //

/**
* Sets the y-axis tick format.
*
* @private
* @param {(string|null)} fmt - axis tick format
* @throws {TypeError} must be a string primitive or null
*/
function set( fmt ) {
	/*jshint validthis: true */ // TODO: eslint
	if ( !isString( fmt ) && !isNull( fmt ) ) {
		throw new TypeError( 'invalid value. `yTickFormat` must be a string primitive or null. Value: `' + fmt + '`.' );
	}
	debug( 'Current value: %s.', this._yTickFormat );

	this._yTickFormat = fmt;
	debug( 'New value: %s.', this._yTickFormat );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
