'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:yvalue' );
var isFunction = require( '@stdlib/utils/is-function' );


// SET //

/**
* Sets the y-value accessor.
*
* @private
* @param {Function} fcn - accessor
* @throws {TypeError} must be a function
*/
function set( fcn ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isFunction( fcn ) ) {
		throw new TypeError( 'invalid value. `yValue` must be a function. Value: `' + fcn + '`.' );
	}
	debug( 'Current value: %s.', this._yValue );
	oldVal = this._yValue;

	this._yValue = fcn;
	debug( 'New value: %s.', this._yValue );

	this.emit( 'yValueChanged', fcn, oldVal );
} // end FUNCTION set()


// EYPORTS //

module.exports = set;
