'use strict';

// MODULES //

var debug = require( 'debug' )( 'line:accessor:a-value' );


// ACCESSOR //

/**
* Annotation accessor.
*
* @private
* @param {NumericArray} d - datum
* @returns {*} annotation
*/
function aValue( d ) {
	var v = d[ 1 ];
	debug( 'Datum: %s. Annotation: %s.', JSON.stringify( d ), v );
    return v;
} // end FUNCTION aValue()


// EXPORTS //

module.exports = aValue;
