'use strict';

// MODULES //

var debug = require( 'debug' )( 'line:engine:components:defs' );
var h = require( 'virtual-dom/h' );
var clipPath = require( './clippath.js' );


// VARIABLES //

var ELEMENT = 'defs';


// RENDER //

/**
* Renders SVG definitions.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var children;
	var props;

	props = {
		'namespace': 'http://www.w3.org/2000/svg'
	};

	debug( 'Rendering clip path...' );
	children = [
		clipPath( ctx )
	];

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, children );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
