'use strict';

// MODULES //

var debug = require( 'debug' )( 'line:engine' );
var EventEmitter = require( 'events' ).EventEmitter;
var shapes = require( 'd3-shape' ); // TODO: remove
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var copy = require( '@stdlib/utils/copy' );
var minstd = require( '@stdlib/math/base/random/minstd' );
var validate = require( './validate.js' );
var defaults = require( './defaults.json' );


// LINE ENGINE //

/**
* Line engine constructor.
*
* @constructor
* @param {Object} state - engine state
* @param {Options} [options] - engine options
* @throws {TypeError} must provide valid options
* @returns {LineEngine} engine instance
*/
function LineEngine( state, options ) {
	var self;
	var opts;
	var err;
	if ( !( this instanceof LineEngine ) ) {
		if ( arguments.length > 1 ) {
			return new LineEngine( state, options );
		}
		return new LineEngine( state );
	}
	self = this;
	opts = copy( defaults );
	if ( arguments.length > 1 ) {
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	debug( 'Creating an engine with the following configuration: %s.', JSON.stringify( opts ) );
	EventEmitter.call( this );

	// Public, read-only properties...
	setReadOnly( this, 'state', state );
	setReadOnly( this, 'line', shapes.line() ); // TODO: make a prototype method which generates a line generator

	// "Private" properties...
	Object.defineProperty( this, '_opts', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': opts
	});
	Object.defineProperty( this, '__uid__', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': minstd().toString() // TODO: uuid
	});
	Object.defineProperty( this, '_clipPathID', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': minstd().toString() // TODO: uuid
	});

	// Bind a listener to update on state changes:
	this.state.on( 'change', onChange );

	return this;

	function onChange() {
		debug( 'Received a change event.' );
		self.render();
	}
} // end FUNCTION LineEngine()

/*
* Create a prototype which inherits from the parent prototype.
*/
LineEngine.prototype = Object.create( EventEmitter.prototype );

/*
* Set the constructor.
*/
LineEngine.prototype.constructor = LineEngine;

/**
* SVG transform for translating an x-axis.
*
* @memberof LineEngine.prototype
* @type {string}
* @name xAxisTransform
*/
Object.defineProperty( LineEngine.prototype, 'xAxisTransform', {
	'configurable': false,
	'enumerable': true,
	'get': require( './props/x-axis-transform/get.js' )
});

/**
* SVG transform for translating a y-axis.
*
* @memberof LineEngine.prototype
* @type {string}
* @name yAxisTransform
*/
Object.defineProperty( LineEngine.prototype, 'yAxisTransform', {
	'configurable': false,
	'enumerable': true,
	'get': require( './props/y-axis-transform/get.js' )
});

/**
* Function to generate triangles as SVG paths.
*
* @memberof LineEngine.prototype
* @type {Function}
* @name triangle
*/
Object.defineProperty( LineEngine.prototype, 'triangle', {
	'configurable': false,
	'enumerable': true,
	'get': require( './props/triangle/get.js' )
});

/**
* Function to generate vertical lines.
*
* @memberof LineEngine.prototype
* @type {Function}
* @name vline
*/
Object.defineProperty( LineEngine.prototype, 'vline', {
	'configurable': false,
	'enumerable': true,
	'get': require( './props/vline/get.js' )
});

/**
* Renders a plot.
*
* @memberof LineEngine.prototype
* @type {Function}
* @name render
* @returns {VTree} virtual tree
*/
setReadOnly( LineEngine.prototype, 'render', require( './methods/render.js' ) );


// EXPORTS //

module.exports = LineEngine;
