'use strict';

// MODULES //

var debug = require( 'debug' )( 'line:engine:render' );
var plot = require( './../components/plot.js' );


// RENDER //

/**
* Renders a plot.
*
* @private
* @returns {VTree} virtual tree
*/
function render() {
	/* jshint validthis: true */ // TODO: eslint
	var vtree;

	debug( 'Rendering...' );

	// Generate the plot:
	vtree = plot( this );

	// Announce that a new tree has been rendered:
	this.emit( 'render', vtree );

	return vtree;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
