'use strict';

// MODULES //

var isNull = require( '@stdlib/utils/is-null' );
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;


/**
* Validates `yMax`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if (
		!isNull( v ) &&
		!isNumber( v ) // FIXME: finite number
	) {
		return new TypeError( 'invalid value. `yMax` must be either a finite number or null. Value: `' + v + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
