'use strict';

// MODULES //

var copy = require( '@stdlib/utils/copy' );
var timeseries = require( '@stdlib/plot/time-series' );
var defaults = require( './defaults.json' );
var validate = require( './validate.js' );


// PLOT //

/**
* Plots data.
*
* @param {NumericArray} x - x data
* @param {Array<NumericArray>} y - y data
* @param {Options} [options] - plot options
* TODO
* @throws {TypeError} must provide valid options
* @returns {Plot} plot instance
*
* @example
* TODO
*/
function plot( x, y, options ) {
	var opts;
	var err;

	opts = copy( defaults );
	if ( arguments.length > 2 ) {
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	if ( opts.type === 'timeseries' ) {
		return timeseries({
			'x': x,
			'y': y,
			'width': opts.width,
			'height': opts.height,
			'engine': opts.engine
		});
	}
} // end FUNCTION plot()


// EXPORTS //

module.exports = plot;
