'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var COLORS = require( './../../etc/colors.js' );


// GET //

/**
* Returns the data colors.
*
* @private
* @returns {StringArray} colors
*/
function get() {
	/* jshint validthis: true */ // TODO: eslint
	if ( isString( this._colors ) ) {
		return COLORS[ this._colors ].slice();
	}
	return this._colors.slice();
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
