'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:set:colors' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var isStringArray = require( '@stdlib/utils/is-string' ).isPrimitiveStringArray;
var COLORS = require( './colors.js' );


// SET //

/**
* Sets the data colors.
*
* @private
* @param {(string|StringArray)} colors - data colors
* @throws {TypeError} must be either a string or an array of strings
* @throws {Error} must be a recognized color set
*/
function set( colors ) {
	/*jshint validthis: true */ // TODO: eslint
	var isStr = isString( colors );
	if (
		!isStr &&
		!isStringArray( colors )
	) {
		throw new TypeError( 'invalid value. `colors` must be either a string or a string array. Value: `' + colors + '.`' );
	}
	if ( isStr ) {
		if ( COLORS[ colors ] === void 0 ) {
			throw new Error( 'invalid value. `colors` must be a recognized color set. Value: `' + colors + '`.' );
		}
	} else {
		colors = colors.slice();
	}
	debug( 'Current value: %s.', JSON.stringify( this._colors ) );


	this._colors = colors;
	debug( 'New Value: %s.', JSON.stringify( this._colors ) );

	this.emit( 'change' );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
