'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:set:line-styles' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var isStringArray = require( '@stdlib/utils/is-string' ).isPrimitiveStringArray;
var indexOf = require( '@stdlib/utils/indexof' );
var LINESTYLES = require( './line_styles.json' );


// SET //

/**
* Sets the data line styles.
*
* @private
* @param {(string|StringArray)} v - line styles
* @throws {TypeError} must be a string or string array
* @throws {Error} must be a supported line style
*/
function set( v ) {
	/*jshint validthis: true */ // TODO: eslint
	var isStr = isString( v );
	var i;
	if (
		!isStr &&
		!isStringArray( v )
	) {
		throw new TypeError( 'invalid value. `lineStyles` must be a string or a string array. Value: `'+v+'.`' );
	}
	if ( isStr ) {
		v = [ v ];
	} else {
		v = v.slice();
	}
	for ( i = 0; i < v.length; i++ ) {
		if ( indexOf( LINESTYLES, v[i] ) === -1 ) {
			throw new Error( 'invalid value. Unsupported/unrecognized line style. Value: `['+v.join(',')+']`.' );
		}
	}
	debug( 'Current value: %s.', JSON.stringify( this._lineStyles ) );

	this._lineStyles = v;
	debug( 'New Value: %s.', JSON.stringify( this._lineStyles ) );

	this.emit( 'change' );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
