'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:set:line-styles' );
var isStringArray = require( '@stdlib/utils/is-string' ).isPrimitiveStringArray;
var indexOf = require( '@stdlib/utils/indexof' );
var LINESTYLES = require( './line_styles.json' );


// SET //

/**
* Sets the data line styles.
*
* @private
* @param {StringArray} arr - line styles
* @throws {TypeError} must be a string array
* @throws {Error} must only contain supported line styles
*/
function set( arr ) {
	/*jshint validthis: true */ // TODO: eslint
	var i;
	if ( !isStringArray( arr ) ) {
		throw new TypeError( 'invalid value. `lineStyles` must be a string array. Value: `'+arr+'.`' );
	}
	for ( i = 0; i < arr.length; i++ ) {
		if ( indexOf( LINESTYLES, arr[i] ) === -1 ) {
			throw new Error( 'invalid value. `lineStyles` must only contain supported line styles. Value: `['+arr.join(',')+']`.' );
		}
	}
	debug( 'Current value: %s.', JSON.stringify( this._lineStyles ) );

	this._lineStyles = arr.slice();
	debug( 'New Value: %s.', JSON.stringify( this._lineStyles ) );

	this.emit( 'change' );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
