'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:set:symbol-size' );
var events = require( './../../events' );
var isValid = require( './../../validators/symbol_size.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'symbolSize' );


// SET //

/**
* Sets the symbol size.
*
* @private
* @param {NonNegativeInteger} size - size
* @throws {TypeError} must be a nonnegative integer
*/
function set( size ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( size );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %d.', this._size );

	this._size = size;
	debug( 'New Value: %d.', this._size );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
