'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:set:viewer' );
var events = require( './../../events' );
var isValid = require( './../../validators/viewer.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'viewer' );


// SET //

/**
* Sets the viewer.
*
* @private
* @param {string} viewer - viewer
* @throws {TypeError} must be a recognized viewer
*/
function set( viewer ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( viewer );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._viewer );

	this._viewer = viewer;
	debug( 'New value: %s.', this._viewer );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
