'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:set:x-rug' );
var isBoolean = require( '@stdlib/utils/is-boolean' ).isPrimitive;


// SET //

/**
* Sets a flag indicating whether to display a rug plot along the x-axis.
*
* @private
* @param {boolean} bool - boolean indicating whether to display a rug plot along the x-axis
* @throws {TypeError} must be a boolean primitive
*/
function set( bool ) {
	/*jshint validthis: true */ // TODO: eslint
	if ( !isBoolean( bool ) ) {
		throw new TypeError( 'invalid value. `xRug` must be a boolean primitive. Value: `' + bool + '.`' );
	}
	debug( 'Current value: %d.', this._xRug );

	this._xRug = bool;
	debug( 'New Value: %d.', this._xRug );

	this.emit( 'change' );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
