'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:set:xscale' );
var events = require( './../../events' );
var isValid = require( './../../validators/x_scale.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'xScale' );


// SET //

/**
* Sets the x-axis scale.
*
* @private
* @param {string} scale - axis scale
* @throws {TypeError} must be a string primitive
*/
function set( scale ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( scale );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._xScale );

	this._xScale = scale;
	debug( 'New value: %s.', this._xScale );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
