'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:render:svg:init' );
var Annotations = require( '@stdlib/plot/components/svg/annotations' );
var ClipPath = require( '@stdlib/plot/components/svg/clip-path' );
var Canvas = require( '@stdlib/plot/components/svg/canvas' );
var Graph = require( '@stdlib/plot/components/svg/graph' );
var Title = require( '@stdlib/plot/components/svg/title' );
var Marks = require( '@stdlib/plot/components/svg/marks' );
var Bkgd = require( '@stdlib/plot/components/svg/background' );
var Defs = require( '@stdlib/plot/components/svg/defs' );
var Axis = require( '@stdlib/plot/components/svg/axis' );


// INIT //

/**
* Initializes SVG components.
*
* @private
* @param {Object} state - state
*/
function init( state ) {
	var svg = state.$.svg;

	debug( 'Initializing components...' );

	debug( 'Initializing canvas...' );
	Object.defineProperty( svg, 'canvas', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Canvas({
			'autoRender': false
		})
	});

	debug( 'Initializing definitions...' );
	Object.defineProperty( svg, 'defs', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Defs({
			'autoRender': false
		})
	});

	debug( 'Initializing clipping path...' );
	Object.defineProperty( svg, 'clipPath', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new ClipPath({
			'autoRender': false,
			'id': state._clipPathId
		})
	});

	debug( 'Initializing graph...' );
	Object.defineProperty( svg, 'graph', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Graph({
			'autoRender': false
		})
	});

	debug( 'Initializing annotations...' );
	Object.defineProperty( svg, 'annotations', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Annotations({
			'autoRender': false
		})
	});

	debug( 'Initializing title...' );
	Object.defineProperty( svg, 'title', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Title({
			'autoRender': false
		})
	});

	debug( 'Initializing background...' );
	Object.defineProperty( svg, 'bkgd', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Bkgd({
			'autoRender': false
		})
	});

	debug( 'Initializing marks...' );
	Object.defineProperty( svg, 'marks', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Marks({
			'autoRender': false,
			'clipPathId': state._clipPathId
		})
	});

	debug( 'Initializing x-axis...' );
	Object.defineProperty( svg, 'xAxis', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Axis({
			'autoRender': false
		})
	});

	debug( 'Initializing y-axis...' );
	Object.defineProperty( svg, 'yAxis', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': new Axis({
			'autoRender': false
		})
	});

	debug( 'All components initialized.' );
} // end FUNCTION init()


// EXPORTS //

module.exports = init;
