'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:render:svg:marks:lines' );


// RENDER //

/**
* Renders line marks.
*
* @private
* @param {Object} state - state
* @returns {VTree} virtual tree
*/
function render( state ) {
	var lineStyle;
	var nColors;
	var nStyles;
	var color;
	var marks;
	var line;
	var len;
	var i;

	line = state.$.svg.path;
	nStyles = state.lineStyles.length;
	nColors = state.colors.length;

	len = state.x.length;
	marks = [];

	debug( 'Rendering lines...' );
	for ( i = 0; i < len; i++ ) {
		lineStyle = state.lineStyles[ i%nStyles ];
		debug( 'Line style: %s (%d).', lineStyle, i );

		color = state.colors[ i%nColors ];
		debug( 'Line color: %s (%d).', color, i );

		if ( lineStyle === '-' ) {
			line.x = state.x[ i ];
			line.y = state.y[ i ];
			line.label = state.labels[ i ] || '';
			line.color = color;
			// TODO: line opacity

			debug( 'Rendering line %d...', i );
			marks.push( line.render() );
		}
	}
	debug( 'Finished rendering lines.' );
	return marks;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
