'use strict';

/**
* Computes a minimum value.
*
* @private
* @param {NumericArray} arr - input array
* @returns {(number|null)} minimum value or null
*/
function getMin( arr ) {
	var min;
	var i;
	if ( arr.length === 0 ) {
		return null;
	}
	min = arr[ 0 ];
	for ( i = 1; i < arr.length; i++ ) {
		if ( arr[ i ] < min ) {
			min = arr[ i ];
		}
	}
	return min;
} // end FUNCTION getMin


// EXPORTS //

module.exports = getMin;
