'use strict';

// MODULES //

var getKeys = require( 'object-keys' ).shim();
var isObject = require( '@stdlib/utils/is-object' ); // TODO: plain object
var validators = require( './validators' );


// VARIABLES //

var KEYS = getKeys( validators );


// VALIDATE //

/**
* Validates function options.
*
* @private
* @param {Options} options - function options
* @param {Array} [options.x] - x-values
* @param {Array} [options.y] - y-values
* @param {(StringArray|EmptyArray)} [options.labels] - data labels
* @param {(string|StringArray)} [options.colors] - data colors
* @param {StringArray} [options.lineStyles] - data line styles
* @param {StringArray} [options.symbols] - data symbols
* @param {NonNegativeInteger} [options.symbolSize] - symbol size
* @param {Function} [options.isDefined] - accessor defining whether a datum is defined
* @param {PositiveNumber} [options.width] - plot width
* @param {PositiveNumber} [options.height] - plot height
* @param {NonNegativeInteger} [options.paddingLeft] - left padding
* @param {NonNegativeInteger} [options.paddingRight] - right padding
* @param {NonNegativeInteger} [options.paddingTop] - top padding
* @param {NonNegativeInteger} [options.paddingBottom] - bottom padding
* @param {(Date|FiniteNumber|null)} [options.xMin] - minimum value of x-axis domain
* @param {(Date|FiniteNumber|null)} [options.xMax] - maximum value of x-axis domain
* @param {(FiniteNumber|null)} [options.yMin] - minimum value of y-axis domain
* @param {(FiniteNumber|null)} [options.yMax] - maximum value of y-axis domain
* @param {string} [options.xScale] - x-axis scale
* @param {string} [options.yScale] - y-axis scale
* @param {(string|null)} [options.xTickFormat] - x-axis tick format
* @param {(string|null)} [options.yTickFormat] - y-axis tick format
* @param {(NonNegativeInteger|null)} [options.xNumTicks] - number of x-axis tick marks
* @param {(NonNegativeInteger|null)} [options.yNumTicks] - number of y-axis tick marks
* @param {string} [options.xAxisOrient] - x-axis orientation
* @param {string} [options.yAxisOrient] - y-axis orientation
* @param {string} [options.title] - plot title
* @param {string} [options.xLabel] - x-axis label
* @param {string} [options.yLabel] - y-axis label
* @param {string} [options.engine] - plot engine
* @param {string} [options.viewer] - plot viewer
* @param {boolean} [options.autoRender] - indicates whether to re-render on a change event
* @param {boolean} [options.autoView] - indicates whether to generate an updated view on a render event
* @returns {(Error|null)} error or null
*
* @example
* var options = {
*     'width': 720,
*     'height': 500,
*     'engine': 'svg'
* };
* var err = validate( options );
* if ( err ) {
*     throw err;
* }
*/
function validate( options ) {
	var err;
	var key;
	var val;
	var i;
	if ( !isObject( options ) ) {
		return new TypeError( 'invalid input argument. Options argument must be an object. Value: `' + options + '`.' );
	}
	for ( i = 0; i < KEYS.length; i++ ) {
		key = KEYS[ i ];
		if ( options.hasOwnProperty( key ) ) {
			val = options[ key ];
			err = validators[ key ]( val );
			if ( err ) {
				return err;
			}
		}
	}
	return null;
} // end FUNCTION validate()


// EXPORTS //

module.exports = validate;
