'use strict';

// MODULES //

var isStringArray = require( '@stdlib/utils/is-string' ).isPrimitiveStringArray;
var indexOf = require( '@stdlib/utils/indexof' );
var SYMBOLS = require( './../etc/symbols.json' );


// TEST //


/**
* Validates `symbols`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	var i;
	if ( !isStringArray( v ) ) {
		return new TypeError( 'invalid value. `symbols` must be a string array. Value: `'+v+'.`' );
	}
	for ( i = 0; i < v.length; i++ ) {
		if ( indexOf( SYMBOLS, v[i] ) === -1 ) {
			return new Error( 'invalid value. `symbols` must only contain supported symbols. Value: `['+v.join(',')+']`.' );
		}
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
