'use strict';

// MODULES //

var indexOf = require( '@stdlib/utils/indexof' );
var VIEWERS = require( './../etc/viewers.json' );


// TEST //

/**
* Validates `viewer`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if ( indexOf( VIEWERS, v ) === -1 ) {
		return new TypeError( 'invalid value. Unrecognized/unsupported `viewer`. Value: `' + v + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
