'use strict';

// MODULES //

var debug = require( 'debug' )( 'plot:view' );
var isValid = require( './../validators/viewer.js' );
var _view = require( './view.js' );


// VIEW //

/**
* Generates a plot view.
*
* @private
* @param {string} viewer - plot viewer
*/
function view( viewer ) {
	/* jshint validthis: true */ // TODO: eslint
	var _viewer;
	var err;
	if ( arguments.length ) {
		err = isValid( viewer );
		if ( err ) {
			throw err;
		}
		_viewer = viewer;
	} else {
		_viewer = this._viewer;
	}
	debug( 'Viewer: %s.', _viewer );
	debug( 'Generating view...' );
	_view( _viewer, this.render() );
} // end FUNCTION view()


// EXPORTS //

module.exports = view;
