'use strict';

// MODULES //

var stdout = require( './stdout' );
var browser = require( './browser' );


// VIEW //

/**
* Generates a plot view.
*
* @private
* @param {string} viewer - plot viewer
* @param {*} plot - rendered plot
*/
function view( viewer, plot ) {
	if ( viewer === 'none' ) {
		return;
	}
	if ( viewer === 'stdout' ) {
		return stdout( plot );
	}
	if ( viewer === 'browser' ) {
		return browser( plot );
	}
	if ( viewer === 'terminal' ) {
		// TODO: ASCII
		return;
	}
	// TODO: Electron
} // end FUNCTION view()


// EXPORTS //

module.exports = view;
