'use strict';

// MODULES //

var logger = require( 'debug' );
var isTypedArray = require( '@stdlib/assert/is-typed-array' );
var isArray = require( '@stdlib/assert/is-array' );
var formatData = require( './format_data.js' );


// VARIABLES //

var debug = logger( 'sparkline:set:data' );


// MAIN //

/**
* Sets the chart `y` values.
*
* @private
* @param {(Array|TypedArray)} data - `y` values
* @throws {TypeError} must be an array or typed array
* @throws {Error} length must not exceed maximum sliding window size
*/
function set( data ) {
	/* eslint-disable no-invalid-this */
	if (
		!isArray( data ) &&
		!isTypedArray( data ) // TODO: add support for ndarray
	) {
		throw new TypeError( 'invalid value. `data` must be an array or typed array. Value: `' + data + '`.' );
	}
	if ( data.length > this._window ) {
		throw new Error( 'invalid value. `data` length exceeds maximum sliding window size. Window: `' + this._window + '`. Length: `' + data.length + '`.' );
	}
	debug( 'Current value: %s.', JSON.stringify( this._data ) );

	this._data = formatData( data );
	debug( 'New value: %s.', JSON.stringify( this._data ) );

	this.emit( 'change' );
}


// EXPORTS //

module.exports = set;
