'use strict';

// MODULES //

var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var isNull = require( '@stdlib/utils/is-null' );


// SET YMAX //

/**
* Sets the maximum value of the y-axis domain.
*
* @private
* @param {(FiniteNumber|null)} max - maximum value
* @throws {TypeError} must be a finite number primitive or null
*/
function setYMax( max ) {
	/*jshint validthis: true */ // TODO: eslint

	// FIXME: check for finite number primitive
	if ( !isNumber( max ) && !isNull( max ) ) {
		throw new TypeError( 'invalid value. `yMax` must be a finite number primitive or null. Value: `' + max + '`.' );
	}
	this._yMax = max;
} // end FUNCTION setYMax()


// EXPORTS //

module.exports = setYMax;
