'use strict';

// MODULES //

var tape = require( 'tape' );
var isBoolean = require( '@stdlib/assert/is-boolean' ).isPrimitive;
var Sparkline = require( './../lib' );
var get = require( './../lib/get_infinities.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof get, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a boolean', function test( t ) {
	var context;
	var opts;
	var v;

	opts = {
		'infinities': true
	};
	context = new Sparkline( opts );
	v = get.call( context );

	t.equal( isBoolean( v ), true, 'returns a boolean' );
	t.equal( v, opts.infinities, 'returns expected value' );

	t.end();
});
