'use strict';

// MODULES //

var tape = require( 'tape' );
var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var Sparkline = require( './../lib' );
var get = require( './../lib/get_window.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof get, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a positive integer', function test( t ) {
	var context;
	var opts;
	var v;

	opts = {
		'window': 20
	};
	context = new Sparkline( opts );
	v = get.call( context );

	t.equal( isPositiveInteger( v ), true, 'returns a positive integer' );
	t.equal( v, opts.window, 'returns expected value' );

	t.end();
});
