'use strict';

// MODULES //

var tape = require( 'tape' );
var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;
var Sparkline = require( './../lib' );
var get = require( './../lib/get_ymax.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof get, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a number', function test( t ) {
	var context;
	var opts;
	var v;

	opts = {
		'yMax': 3.14
	};
	context = new Sparkline( opts );
	v = get.call( context );

	t.equal( isNumber( v ), true, 'returns a number' );
	t.equal( v, opts.yMax, 'returns expected value' );

	t.end();
});
