'use strict';

// MODULES //

var tape = require( 'tape' );
var isFunction = require( '@stdlib/utils/is-function' );
var noop = require( '@stdlib/utils/noop' );
var Sparkline = require( './../lib' );
var get = require( './../lib/get_yvalue.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof get, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a function', function test( t ) {
	var context;
	var opts;
	var v;

	opts = {
		'yValue': noop
	};
	context = new Sparkline( opts );
	v = get.call( context );

	t.equal( isFunction( v ), true, 'returns a function' );
	t.equal( v, opts.yValue, 'returns expected value' );

	t.end();
});
