'use strict';

// MODULES //

var tape = require( 'tape' );
var Sparkline = require( './../lib' );
var render = require( './../lib/render.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof render, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error (child classes are expected to implement this function)', function test( t ) {
	var sparkline = new Sparkline();

	t.throws( foo, Error, 'throws an error' );
	t.end();

	function foo() {
		render.call( sparkline );
	}
});
