'use strict';

// MODULES //

var tape = require( 'tape' );
var Sparkline = require( './../lib' );
var set = require( './../lib/set_ymax.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof set, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided a finite number primitive or null', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		// NaN, // TODO: uncomment once validate for finite number
		true,
		undefined,
		{},
		[],
		function(){}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();
	function badValue( value ) {
		return function badValue() {
			set.call( {}, value );
		};
	}
});

tape( 'the function sets the maximum of the y-axis domain', function test( t ) {
	var context;
	var v;

	context = new Sparkline();

	set.call( context, 3.14 );
	v = context.yMax;

	t.equal( v, 3.14, 'sets the value' );

	set.call( context, null );
	v = context.yMax;

	t.equal( v, null, 'sets the value' );

	t.end();
});
