'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var columnChart = require( './../lib' );

var chart;
var data;
var opts;
var str;
var i;

// Generate some random data...
data = new Float64Array( 50 );
for ( i = 0; i < data.length; i++ ) {
	data[ i ] = randu() * 100.0;
}

// Set chart options:
opts = {
	'data': data,
	'infinities': true
};

// Create a new column chart:
chart = columnChart( opts );

// Render the chart as a string:
str = chart.render();

console.log( str );
// => '...'
