'use strict';

var tristate = require( './../lib' );

var chart;
var data;
var opts;
var str;
var d;
var i;

// Generate some random data...
data = new Int8Array( 50 );
for ( i = 0; i < data.length; i++ ) {
	d = Math.random();
	if ( d > 0.67 ) {
		d = 1;
	} else if ( d < 0.33 ) {
		d = -1;
	} else {
		d = 0;
	}
	data[ i ] = d;
}

// Set chart options:
opts = {
	'data': data
};

// Create a new tristate chart:
chart = tristate( opts );

// Render the chart as a string:
str = chart.render();

console.log( str );
// => '...'
