'use strict';

var randu = require( '@stdlib/random/base/randu' );
var Int8Array = require( '@stdlib/types/array/int8' );
var winloss = require( './../lib' );

var chart;
var data;
var opts;
var str;
var d;
var i;

// Generate some random data (based on GS Warriors 2015-2016)...
data = new Int8Array( 50 );
for ( i = 0; i < data.length; i++ ) {
	d = randu();
	if ( d > 0.5 ) {       // home
		d = randu();
		if ( d < 0.057 ) { // loss
			d = -2;
		} else {           // win
			d = 2;
		}
	} else {               // away
		d = randu();
		if ( d < 0.229 ) { // loss
			d = -1;
		} else {           // win
			d = 1;
		}
	}
	data[ i ] = d;
}

// Set chart options:
opts = {
	'data': data
};

// Create a new win/loss chart:
chart = winloss( opts );

// Render the chart as a string:
str = chart.render();

console.log( str );
// => '...'
