'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:background' );
var h = require( 'virtual-dom/h' );


// VARIABLES //

var ELEMENT = 'rect';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'className': 'background',
	'attributes': {
		'x': 0,
		'y': 0,
		'width': null,
		'height': null
	}
};
var CHILDREN = [];


// RENDER //

/**
* Renders a plot background.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	PROPS.attributes.width = ctx.state.graphWidth();
	PROPS.attributes.height = ctx.state.graphHeight();

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, CHILDREN );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
