'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:clippath' );
var h = require( 'virtual-dom/h' );
var rect = require( './clippath_rect.js' );


// VARIABLES //

var ELEMENT = 'clipPath';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'id': null
};
var CHILDREN = new Array( 1 );


// CLIP PATH //

/**
* Returns a clip path as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function clipPath( ctx ) {
	PROPS.id = ctx._clipPathID;

	debug( 'Creating clip path rectangle...' );
	CHILDREN[ 0 ] = rect( ctx );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, CHILDREN );
} // end FUNCTION clipPath()


// EXPORTS //

module.exports = clipPath;
