'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:clippath-rect' );
var h = require( 'virtual-dom/h' );


// VARIABLES //

var ELEMENT = 'rect';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'className': 'clipPath',
	'attributes': {
		'width': null,
		'height': null
	}
};
var CHILDREN = [];


// RECT //

/**
* Returns a clip path rectangle as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function rect( ctx ) {
	PROPS.attributes.width = ctx.graphWidth();
	PROPS.attributes.height = ctx.graphHeight();

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, CHILDREN );
} // end FUNCTION rect()


// EXPORTS //

module.exports = rect;
