'use strict';

// MODULES //

var h = require( 'virtual-dom/h' );
var clipPath = require( './clippath.js' );


// SVG DEFINITIONS //

/**
* Returns an SVG definition element as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function defs( ctx ) {
	var children;
	var opts;

	opts = {
		'namespace': 'http://www.w3.org/2000/svg'
	};

	children = [
		clipPath( ctx )
	];

	return h( 'defs', opts, children );
} // end FUNCTION defs()


// EXPORTS //

module.exports = defs;
