'use strict';

// MODULES //

var h = require( 'virtual-dom/h' );
var bkgd = require( './background.js' );
var marks = require( './marks.js' );


// GRAPH //

/**
* Returns a graph as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function graph( ctx ) {
	var children;
	var opts;

	opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'graph',
		'className': 'graph',
		'attributes': {
			'data-graph-type': 'time-series',
			'transform': 'translate('+ctx.state.paddingLeft+','+ctx.state.paddingTop+')'
		}
	};

	children = [
		bkgd( ctx ),
		marks( ctx )
	];

	return h( 'g', opts, children );
} // end FUNCTION graph()


// EXPORTS //

module.exports = graph;
