'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:graph' );
var h = require( 'virtual-dom/h' );
var bkgd = require( './background.js' );
var marks = require( './marks.js' );


// VARIABLES //

var ELEMENT = 'g';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'property': 'graph',
	'className': 'graph',
	'attributes': {
		'data-graph-type': 'time-series',
		'transform': null
	}
};
var CHILDREN = new Array( 2 );


// RENDER //

/**
* Returns a graph as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	PROPS.attributes.transform = 'translate('+ctx.state.paddingLeft+','+ctx.state.paddingTop+')';

	debug( 'Rendering background...' );
	CHILDREN[ 0 ] = bkgd( ctx );

	debug( 'Rendering marks...' );
	CHILDREN[ 1 ] = marks( ctx );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, CHILDREN );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
