'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:marks' );
var h = require( 'virtual-dom/h' );
var path = require( './path.js' );


// VARIABLES //

var ELEMENT = 'g';


// RENDER //

/**
* Renders plot marks.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var children;
	var xScale;
	var yScale;
	var labels;
	var label;
	var props;
	var tmp;
	var len;
	var x;
	var y;
	var i;
	var j;

	props = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'marks',
		'className': 'marks',
		'attributes': {
			'clip-path': 'url(#'+ctx._clipPathID+')'
		}
	};

	xScale = ctx.state.xScale;
	yScale = ctx.state.yScale;
	x = ctx.state.x;
	y = ctx.state.y;
	labels = ctx.state.labels;

	if ( x.length === 0 || y.length === 0 ) {
		debug( 'Missing `x` and/or `y` data. Unable to render paths.' );
		debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );
		return h( ELEMENT, props, [] );
	}
	debug( 'Rendering paths...' );
	children = [];

	// FIXME: because of how D3 works, we need to create temporary arrays to generate paths. :( We should roll our own SVG path generation; see `d3-shape`.
	// TODO: log path creation
	function xPos( d ) {
		return xScale( d[ 0 ] );
	}
	function yPos( d ) {
		return yScale( d[ 1 ] );
	}
	function defined() {
		return true;
	}
	ctx.line
		.x( xPos ) // TODO: should come from state
		.y( yPos ) // TODO: should come from state
		.defined( defined );
		// .interpolate( ctx.state.interpolation ) // TODO: provide curve factory
		// .tension( ctx.state.tension );

	len = x.length;

	// Only generate the array once...
	tmp = new Array( len );
	for ( i = 0; i < len; i++ ) {
		tmp[ i ] = new Array( 2 );
		tmp[ i ][ 0 ] = x[ i ];
		tmp[ i ][ 1 ] = y[ 0 ][ i ];
	}
	label = labels[ 0 ] || '';
	children.push( path( ctx, tmp, '', label ) ); // FIXME: color

	// Mutate the temp array after...
	for ( j = 1; j < y.length; j++ ) {
		for ( i = 0; i < len; i++ ) {
			tmp[ i ][ 1 ] = y[ j ][ i ];
		}
		label = labels[ j ] || '';
		children.push( path( ctx, tmp, '', label ) ); // FIXME: color
	}
	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, children );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
