'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:meta' );
var h = require( 'virtual-dom/h' );
var plotTitle = require( './title.js' );


// ELEMENTS //

var ELEMENT = 'g';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'property': 'meta',
	'className': 'meta',
	'attributes': {
		'data-graph-type': 'time-series',
		'transform': 'translate(0,0)'
	}
};
var CHILDREN = new Array( 1 );


// RENDER //

/**
* Returns an SVG element containing plot meta information as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	debug( 'Rendering plot title...' );
	CHILDREN[ 0 ] = plotTitle( ctx );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, CHILDREN );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
