'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:path' );
var h = require( 'virtual-dom/h' );


// VARIABLES //

var ELEMENT = 'path';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'property': 'line time-series',
	'className': 'line',
	'attributes': {
		'data-label': null,
		'data-color': null,
		'd': null
	}
};
var CHILDREN = [];


// RENDER //

/**
* Renders an SVG path.
*
* @private
* @param {Object} ctx - context
* @param {Array} data - data
* @param {string} label - path label
* @param {string} color - path color
* @returns {VTree} virtual tree
*/
function render( ctx, data, label, color ) {
	PROPS.attributes[ 'data-label' ] = label;
	PROPS.attributes[ 'data-color' ] = color;
	PROPS.attributes[ 'd' ] = ctx.line( data );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, CHILDREN );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
