'use strict';

// MODULES //

var h = require( 'virtual-dom/h' );
var defs = require ('./defs.js' );
var graph = require( './graph.js' );
var meta = require( './meta.js' );


// PLOT //

/**
* Plot component returned as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function plot( ctx ) {
	/* jshint validthis: true */ // TODO: eslint
	var children;
	var opts;

	opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'canvas',
		'className': 'canvas',
		'attributes': {
			'width': ctx.state.width,
			'height': ctx.state.height
		}
	};

	children = [
		defs( ctx ),
		graph( ctx ),
		meta( ctx )
	];

	return h( 'svg', opts, children );
} // end FUNCTION plot()


// EXPORTS //

module.exports = plot;
