'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:title' );
var h = require( 'virtual-dom/h' );


// VARIABLES //

var ELEMENT = 'text';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'property': 'plot.title',
	'className': 'title noselect',
	'attributes': {
		'x': 0,
		'y': 0
	}
};


// RENDER //

/**
* Returns a plot title as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var title = ctx.state.title;
	debug( 'Plot title: %s.', title );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );
	return h( ELEMENT, PROPS, title );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
