'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:xaxis' );


// RENDER //

/**
* Renders an x-axis.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var axis;

	debug( 'Generating a virtual DOM tree...' );
	axis = ctx.state.xAxis.render();

	// Update the class names:
	axis.properties.className += ' x';

	// Translate the axis into position:
	axis.properties.attributes.transform = ctx.xAxisTransform;

	return axis;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
