'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:xaxis' );
var xLabel = require( './xlabel.js' );


// RENDER //

/**
* Renders an x-axis.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var label;
	var axis;

	debug( 'Rendering axis label...' );
	label = xLabel( ctx );

	debug( 'Generating a virtual DOM tree...' );
	axis = ctx.state.xAxis().render();

	// Translate the axis into position:
	axis.properties.attributes.transform = ctx.xAxisTranslation();

	// Update the class names:
	axis.properties.className += ' x';

	// Append the label:
	axis.children.push( label );
	axis.count += 1;

	return axis;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
