'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:components:yaxis' );
var yLabel = require( './ylabel.js' );


// RENDER //

/**
* Renders a y-axis.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var label;
	var axis;

	debug( 'Rendering axis label...' );
	label = yLabel( ctx );

	debug( 'Generating a virtual DOM tree...' );
	axis = ctx.state.yAxis.render();

	// Translate the axis into position:
	axis.properties.attributes.transform = ctx.yAxisTranslation();

	// Update the class names:
	axis.properties.className += ' y';

	// Append the label:
	axis.children.push( label );
	axis.count += 1;

	return axis;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
